import React, { useEffect, useState } from 'react';
import './style.css';
import close from '../../assets/imagens/close.svg';
import settigs from '../../assets/imagens/settigs.svg';
import lupa from '../../assets/imagens/lupa.svg';

function Panel(){
    const [medicos, setMedicos] = useState([]); 

    useEffect(() => {
       Listar();
    }, []);

    const Listar = () =>{

        const url = "http://localhost:5000/api/Medico";
        fetch(url,{
            // headers: {
                //   authorization: 'Bearer ' + localStorage.getItem('Gcb-Caren-Token')
                //},
                method: 'GET',
            })
            .then((response) => response.json())
            .then((dados) => {
                setMedicos(dados);
                console.log(dados);
            })
            .catch((error) => console.error(error)); 
    };

    const modal =(id:any)=>{
        alert("foi aqui"+id);
        return(
            <div>
                <h1>hello</h1>
            </div>
        )
    }

    const excluir =(id:any)=>{
        let resposta = window.prompt("Digite 'excluir medico' para excluir esse médico(a):")?.toLowerCase();

        if(resposta == "excluir medico"){
            const url = "http://localhost:5000/api/Medico" + id;
            fetch(url,{
                //headers: {
                  //     authorization: 'Bearer ' + localStorage.getItem('Gcb-Caren-Token')
                    //},
                method: 'DELETE'
            })
            .then(() => {
                Listar();
            })
            .catch(err => {
                console.error(err);
            })
        }
    }

    return(
    <div>
        <div className="barra-cima">
            
        <div className="input-container">
            <input className="input-field" type="text"  placeholder="Digite o nome do médico(a) para encontra-lo:" name="usrnm"/>
            <i class="fa fa-user icon"></i>
        </div>
            <button className="botao-cima" onClick={()=> modal(0)}>Adicionar médico(a)</button>
        </div>
        <div className="container">
  <ul className="responsive-table">
    <li className="table-header">
      <div className="col col-1">Nome do médico(a)</div>
      <div className="col col-2">CRM</div>
      <div className="col col-3">Ver mais</div>
      <div className="col col-4">Deletar</div>
    </li>
    {
    medicos.map((item:any) =>{
        return(
            <li className="table-row">
            <div className="col col-1" data-label="Job Id">{item.nome}</div>
            <div className="col col-2" data-label="Customer Name">{item.crm}</div>
            <div className="col col-3" data-label="Amount"><img className="regular" src={settigs} onClick={()=> modal(item.id)} alt="configurações"/></div>
            <div className="col col-4" data-label="Payment Status"> <img className="regular" src={close} onClick={()=> excluir(item.id)} alt="excluir médico"/></div>
            </li>
            )
        })
    }
  </ul>
</div>
    </div>
    );
}

export default Panel;
