import React, { useEffect, useState } from 'react';
import './style.css';
import close from '../../assets/imagens/close.svg';
import settigs from '../../assets/imagens/settigs.svg';
import lupa from '../../assets/imagens/lupa.svg';

function Panel(){
    const [medicos, setMedicos] = useState([]); 
    const [medicoNome, setmedicoNome] = useState(""); 
    const [showModal, setShowModel] = useState(false); 
    const[usuario, setUsuario] = useState([]);

    useEffect(() => {
       Listar();
    }, []);

    const Listar = () =>{

        const url = "http://localhost:5000/api/Medico";
        fetch(url,{
            // headers: {
                //   authorization: 'Bearer ' + localStorage.getItem('Gcb-Caren-Token')
                //},
                method: 'GET',
            })
            .then((response) => response.json())
            .then((dados) => {
                setMedicos(dados);
                console.log(dados);
            })
            .catch((error) => console.error(error)); 
    };

    const [botao, setBotao] = useState(""); 
    function modal(id:any){
        
        if(id !== null && id !== 0){
            const url = "http://localhost:5000/api/Medico/"+id;
            fetch(url,{
                 //headers: {
                  //     authorization: 'Bearer ' + localStorage.getItem('Gcb-Caren-Token')
                    //},
                    method: 'get'
            })
            .then((response) => response.json())
            .then((dados) => {
                setUsuario(dados);
                console.log(dados);
                setBotao("Atualizar");
            })
            .catch(err => {
                console.error(err);
                setBotao("Atualizar");
            })
            setBotao("Atualizar");
        }else{
            setBotao("Adicionar");
        }

        return(
            <div style={{display: showModal? 'block' : 'none'}}>
                <div id="myModal" className="modal">
                    <div className="modal-content">
                    <span onClick={() => setShowModel(false)} className="close">&times;</span>
                   <div className="containerInputs">
                       <form>

                       <div>
                           <input type="text" />
                           <input type="text" />
                       </div>
                       <input type="email"/>
                       <div>
                           <input type="text" />
                           <input type="text" />
                       </div>

                       <div className="form-pass">
                           <input type="password" />
                           <input type="datetime" name="" id=""/>
                       </div>
                       <button>{botao}</button>
                       </form>
                   </div>
                    </div>
                </div>
            </div>
        )
    }

    const excluir =(id:any)=>{
        let resposta = window.prompt("Digite 'excluir medico' para excluir esse mÃ©dico(a):")?.toLowerCase();

        if(resposta == "excluir medico"){
            const url = "http://localhost:5000/api/Medico" + id;
            fetch(url,{
                //headers: {
                  //     authorization: 'Bearer ' + localStorage.getItem('Gcb-Caren-Token')
                    //},
                method: 'DELETE'
            })
            .then(() => {
                Listar();
            })
            .catch(err => {
                console.error(err);
            })
        }
    }

    function Buscar(nome: string) { 
        if (medicos.length != 0) {
            var buscados = medicos.filter((item: any) => item.nome.toUpperCase().includes(nome.toUpperCase()));
            if (buscados != undefined)
                return buscados; 
        }
        return medicos; 
    }
    return(
        <div>
            {
                modal(null)
            }
        <div className="barra-cima">
            
        <div className="input-container">
            <input value={medicoNome} onChange={e => setmedicoNome(e.target.value)} className="input-field" type="text"  placeholder="Digite o nome do mÃ©dico(a) para encontra-lo:" name="usrnm"/>
            <i className="fas fa-search icon"></i>
        </div>
        <button onClick={()=>{modal(0); setShowModel(true)} } className="btn">Adicionar mÃ©dico(a)<i className="fas fa-plus icone"></i></button>
        </div>
        <div className="container">
  <ul className="responsive-table">
    <li className="table-header">
      <div className="col col-1">Nome do mÃ©dico(a)</div>
      <div className="col col-2">CRM</div>
      <div className="col col-3">Ver mais</div>
      <div className="col col-4">Deletar</div>
    </li>
    {
    Buscar(medicoNome).map((item:any) =>{
        return(
            <li className="table-row">
            <div className="col col-1">{item.nome}</div>
            <div className="col col-2">{item.crm}</div>
            <div className="col col-3"><img className="regular" src={settigs} onClick={()=>{ modal(item.id); setShowModel(true)}} alt="configuraÃ§Ãµes"/></div>
            <div className="col col-4"> <img className="regular" src={close} onClick={()=> excluir(item.id)} alt="excluir mÃ©dico"/></div>
            </li>
            )
        })
    }
  </ul>
</div>
    </div>
    );
}

export default Panel;
